package main.scala.babate

import java.math.BigInteger

class GroupElement(initialValue: BigInt) {
  require(initialValue >= 0)

  val value = initialValue % GroupElement.p

  def +(b: GroupElement) = {
    new GroupElement(((value * b.value) + GroupElement.p) % GroupElement.p)
  }

  def -(b: GroupElement) = {
    this + (~b)
  }

  def unary_~ = {
    new GroupElement(this.value.modPow(GroupElement.p - 2, GroupElement.p))
  }
}

object GroupElement {
  //2048 bits prime from https://www.ietf.org/rfc/rfc3526.txt
  val p = new BigInt(new BigInteger("", 16))
  val g = new GroupElement(2)
}